import './ExpenseForm.css';
import { useState } from 'react';

const ExpenseForm = props => {
    
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredDate, setEnteredDate] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');

    const submitHandler = event => {
        event.preventDefault();
        const newExpenseItem = {
            title: enteredTitle,
            date: new Date(enteredDate),
            amount: enteredAmount
        }
        props.onSaveExpenseData(newExpenseItem);
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
    }

    const titleEventHandler = event => {
        setEnteredTitle(event.target.value);
    }

    const dateEventHandler = event => {
        setEnteredDate(event.target.value);
    }

    const amountEventHandler = event => {
        setEnteredAmount(event.target.value);
    }

    let showForm = false;

    let formContent = <div><button onClick={onClickShowFormHandler}> Add New Expense</button></div>;

    const onClickShowFormHandler = () => {
        console.log("BEFORE : " + showForm);
        showForm == false ? showForm = true : showForm = false;
        console.log("AFTER : " + showForm); 
    }
    


    return (
        <div>
            {formContent}
        </div>
    )
};

export default ExpenseForm;
